import "./featuredProperties.css";
import useFetch from "../../Hooks/useFetch"
import IsLoading from "../spinner/isloading";

const FeaturedProperties = () => {
  const images = [
    "https://cf.bstatic.com/xdata/images/hotel/square600/13125860.webp?k=e148feeb802ac3d28d1391dad9e4cf1e12d9231f897d0b53ca067bde8a9d3355&o=&s=1",
    "https://cf.bstatic.com/xdata/images/hotel/max1280x900/215955381.jpg?k=ff739d1d9e0c8e233f78ee3ced82743ef0355e925df8db7135d83b55a00ca07a&o=&hp=1",
    "https://images.livemint.com/img/2022/11/01/600x338/hotel_1667321862487_1667321888376_1667321888376.jpg",
    "https://cf.bstatic.com/xdata/images/hotel/max1280x900/322658536.jpg?k=3fffe63a365fd0ccdc59210188e55188cdb7448b9ec1ddb71b0843172138ec07&o=&hp=1"
  ]
  const {data,loading} = useFetch("http://localhost:5000/hotels?featured=true&limit=4");
console.log(data)
  return (
    <div className="fp">
      {loading?<div className="loading-container"><IsLoading/></div>:<>{data&&images.map((img,i)=>{
        return <div key={i} className="fpItem">
        <img
          src={img}
          alt=""
          className="fpImg"
        />
        <span className="fpName">{data[i]?.type}</span>
        <span className="fpCity">{data[i]?.city}</span>
        <span className="fpPrice">Starting from $120</span>
        <div className="fpRating">
          <button>8.9</button>
          <span>Excellent</span>
        </div>
      </div>
         
      
      })}</>}
      
    </div>
  );
};

export default FeaturedProperties;
